USE master;
GO

IF EXISTS (SELECT name FROM sys.databases WHERE name = N'LibraryManagement')
BEGIN
    ALTER DATABASE [LibraryManagement] SET SINGLE_USER WITH ROLLBACK IMMEDIATE;
    DROP DATABASE [LibraryManagement];
END
GO

CREATE DATABASE LibraryManagement;
GO

USE LibraryManagement;
GO

CREATE TABLE [dbo].[Role] (
    role_id INT PRIMARY KEY IDENTITY(1,1),
    role_name VARCHAR(255) NOT NULL
);

CREATE TABLE [dbo].[Gender] (
    gender_id INT PRIMARY KEY IDENTITY(1,1),
    gender_name VARCHAR(50) NOT NULL
);

CREATE TABLE [dbo].[User] (
    id INT PRIMARY KEY IDENTITY(1,1),
    email VARCHAR(255) NOT NULL,
    password VARCHAR(255) NOT NULL,
    role_id INT,
    full_name VARCHAR(255) NOT NULL,
    gender_id INT,
    mobile VARCHAR(20),
    profile_picture ntext default(N''),
    is_active BIT NOT NULL,
	borrowcard_id INT NULL,
    CONSTRAINT FK_User_Role FOREIGN KEY (role_id) REFERENCES [dbo].[Role](role_id),
    CONSTRAINT FK_User_Gender FOREIGN KEY (gender_id) REFERENCES [dbo].[Gender](gender_id)
);

CREATE TABLE [dbo].[BookCategory] (
    category_id INT PRIMARY KEY IDENTITY(1,1),
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE [dbo].[BookStatus] (
	status_id INT PRIMARY KEY IDENTITY(1,1),
	status_name VARCHAR(255) NOT NULL
);

CREATE TABLE [dbo].[Book] (
    book_id INT PRIMARY KEY IDENTITY(1,1),
    book_title VARCHAR(255) NOT NULL,
    category_id INT,
    book_status BIT NOT NULL,
    is_featured BIT,
    book_cover ntext default(N''),
    brief_info VARCHAR(255),
    description TEXT,
    CONSTRAINT FK_Book_Category FOREIGN KEY (category_id) REFERENCES [dbo].[BookCategory](category_id)
);

CREATE TABLE [dbo].[BorrowCard] (
    borrowcard_id INT PRIMARY KEY IDENTITY(1,1),
    user_id INT NOT NULL,
	create_date Date NOT NULL,
    CONSTRAINT FK_BorrowCard_User FOREIGN KEY (user_id) REFERENCES [dbo].[User](id),
);

CREATE TABLE [dbo].[BorrowCardContent] (
	borrowcard_id INT NOT NULL,
	book_id INT,
	borrow_date Date NULL,
	return_date Date NULL,
	CONSTRAINT FK_BorrowCardContent_BorrowCard FOREIGN KEY (borrowcard_id) REFERENCES [dbo].[BorrowCard](borrowcard_id),
	CONSTRAINT FK_BorrowCardContent_Book FOREIGN KEY (book_id) REFERENCES [dbo].[Book](book_id),
);

CREATE TABLE [dbo].[Slide] (
    slide_id INT PRIMARY KEY IDENTITY(1,1),
    slide_title VARCHAR(255) NOT NULL,
    slide_image ntext default(N''),
    backlink VARCHAR(255),
    description TEXT,
    is_active BIT NOT NULL
);

